package javaapipkg;

public class MathEx {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		  System.out.println("Math.abs(10)=" + Math.abs(10)); // 절대값
	        System.out.println("Math.abs(-10)=" + Math.abs(-10)); // 절대값
	        System.out.println("Math.ceil(5.4)=" + Math.ceil(5.4)); // 올림
	        System.out.println("Math.ceil(-5.4)=" + Math.ceil(-5.4)); // 올림 -5
	        System.out.println("Math.floor(5.4)=" + Math.floor(5.4)); // 내림
	        System.out.println("Math.floor(-5.4)=" + Math.floor(-5.4)); // 내림 -6
	        System.out.println("Math.max(5,4)=" + Math.max(5,4)); // 큰값
	        System.out.println("Math.max(5.4,5.3)=" + Math.max(5.4,5.3)); // 큰값 5.4
	        System.out.println("Math.min(5,4)=" + Math.min(5,4)); // 작은 값
	        System.out.println("Math.min(5.4,5.3)=" + Math.min(5.4,5.3)); // 작은 값 5.3
	        System.out.println("Math.random()=" + Math.random()); // 0이상 1미만 소숫점
	        System.out.println("Math.rint(4.5)=" + Math.rint(4.5));// 5 짝수 버림 4.0
	        System.out.println("Math.rint(5.5)=" + Math.rint(5.5));// 5 홀수 올림 6.0
	        System.out.println("Math.round(4.5)=" + Math.round(4.5)); //반올림
	        System.out.println("Math.round(5.5)=" + Math.round(5.5)); //반올림
	}

}
